import React from 'react';
import axios from 'axios';

const API_KEY = 'apikey=c606d611';
// const API_URL = 'http://www.omdbapi.com/apikey=c606d611';

// http://www.omdbapi.com/?t=annie&${API_KEY} - gets a single movie e.g. Annie



class Movies extends React.Component {
    state = {
        movie: [],
        query: '',
    }

    //listens to what is typed in the search bar
    handleInput = (event) => {
        event.preventDefault();
        this.setState({
            query: event.target.value
        })
    }

    //submits movie title typed in to search bar
    submitInput = (event) => {
        event.preventDefault();
        axios
        .get(`http://www.omdbapi.com/?s=${this.state.query}&${API_KEY}`)
        .then(response => {
            console.log(response.data)
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        axios
        .get(`http://www.omdbapi.com/?t=annie&${API_KEY}`)
        .then(response => {
            console.log(response.data)
            this.setState({
                movie: response.data
            })
        })
        .catch(error => {
            console.log(error)
        })
    }


    render() {
        return (
            <>
            <h1>The Shoppies</h1>
            <h2>{this.state.movie.Title}</h2>
            <p>{this.state.movie.Year}</p>
            <p>{this.state.movie.Awards}</p>
            <button>Nominate</button>


            <input>
            </input>
            </>
        )
    }
}

export default Movies;
